import { useContext, createContext, useState } from "react";
import "./Context.css";
import Button from "@material-ui/core/Button";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { Toolbar } from "@material-ui/core";

const styles = makeStyles((theme) => ({
//   bar: {
//     backgroundColor: "#CAE4EA",
//     ["@media (max-width:780px)"]: {
//       flexDirection: "column",
//     },
//   },
//   headerOptions: {
//     display: "flex",
//     flex: 1,
//     justifyContent: "flex-end",
//     ["@media (max-width:780px)"]: {
//       flexDirection: "column",
//     },
//   },

  link: {
    textDecoration: "none",
    color: "black",
  },
}));

const themes = {
  light: {
    background: "#DEE4E7",
    color: "blue",
    bar: {
      background:"yellow"
        },
      
  },
  dark: {
    background: "rgb(40, 44, 52)",
    color: "white",
  },
};

const ThemeContext = createContext();

export default function Footer() {
  const classes = styles();
  const [valueTheme, setValueTheme] = useState(themes.dark);
  const theme = useContext(ThemeContext);
  return (
    <ThemeContext.Provider value={valueTheme}>
      <div
      
        style={{ backgroundColor: valueTheme.background }}
      >
        <Content tema={valueTheme} />

        <Toolbar position="sticky" className={classes.bar} style={{ backgroundColor: valueTheme.bar.background }}>
          <div>
            <Router>
              <Button>
                <Link to="/" className={classes.link}>
                  {" "}
                  Beranda
                </Link>
              </Button>

              <Button>
                <Link to="/makanan" className={classes.link}>
                  Menu Makanan
                </Link>
              </Button>

              <Button>
                <Link to="/minuman" className={classes.link} style={{ color: valueTheme.color }}>
                  Menu Minuman
                </Link>
              </Button>
              <button
                className="Button"
                onClick={() =>
                  setValueTheme(
                    valueTheme === themes.light ? themes.dark : themes.light
                  )
                }
              >
                change
              </button>
            </Router>
          </div>
        </Toolbar>
        <div>
          <h1 style={{ color: valueTheme.color }}>hhj</h1>
        </div>
      </div>
    </ThemeContext.Provider>
  );
}

function Content(props) {
  return (
    <div>
      <Text tema={props.tema} />
    </div>
  );
}

function Text(props) {
  const theme = useContext(ThemeContext);
  return <p className="titleContext" style={{ color: theme.text }}></p>;
}
