import { useContext, createContext, useState } from "react";
import "./Context.css";
import Button from "@material-ui/core/Button";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { Toolbar } from "@material-ui/core";

export default function Header() {
  const [show, setShow] = useState(false);
  const [count, setCount] = useState(0);
  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");
  const [valueInput, setValueInput] = useState({
    name: "",
  });
  const countUp = () => setCount(count + 1);
  const countDown = () => setCount(count - 1);
// const handleIdPembelian = (event) => {
//   const { name, value } = event.target;
//   this.setState((state) => {
//     return {
//       [name]: value,
//     };
//   });
// };
const handleIdPembelian = (event, type) => {
  if (type === "name") {
    setValueInput((prevState) => {
      return { ...prevState, name: event.target.value };
    });
  }
};

  return (
    
    <div className="container">
      <div style={{ margin: "10px" }}>
            <label for="idPembelian">Masukkan ID</label>
            <br />
            <input
              onChange={(event) => handleIdPembelian(event, "name")}
              name="idPembelian"
              value={valueInput.name}
              style={{ color: "#6e0234", padding: "5px" }}
            />
          </div>
      {show && (
        <>
          <div className="titleWrapper">
            <p className="title">input with useState</p>
          </div>
          <div className="inputWrapper">
            <div>
              <input
                placeholder="name"
                size="50"
                value={user}
                onChange={(event) => setUser(event.target.value)}
              />
            </div>
            <div>
              <input
                placeholder="password"
                size="50"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
              />
            </div>
          </div>
        </>
      )}
      <button className="Button" onClick={() => setShow(!show)}>
        {show ? "hide" : "show"}
      </button>
      <div style={{ marginBlock: 30 }} />
    </div>
  );
}
