import { useContext, createContext, useState } from "react";
import "./Context.css";
import Button from "@material-ui/core/Button";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { Toolbar } from "@material-ui/core";
import Header from "./Header";
const styles = makeStyles((theme) => ({
    bar:{
        marginTop:"0px"
    },
  line: {
    height: "5px",
    backgroundColor: "	#C0C0C0",
  },
  link: {
    textDecoration: "none",
  },
}));

const themes = {
  light: {
    background: "#f0e6f7",
    color: "#330852",
  },
  dark: {
    background: "rgb(40, 44, 52)",
    color: "white",
  },
};

const ThemeContext = createContext();

export default function Footer() {
  const classes = styles();
  const [valueTheme, setValueTheme] = useState(themes.dark);
  const theme = useContext(ThemeContext);
  return (
    <ThemeContext.Provider value={valueTheme}>
      <div style={{ backgroundColor: valueTheme.background }}>
        <Content tema={valueTheme} />

        <Toolbar position="sticky" className={classes.bar}>
          <div>
            <Router>
              <Button className={classes.btn}>
                <Link
                  to="/"
                  className={classes.link}
                  style={{ color: valueTheme.color, fontWeight: "bold" }}
                >
                  Beranda
                </Link>
              </Button>

              <Button>
                <Link
                  to="/makanan"
                  className={classes.link}
                  style={{ color: valueTheme.color, fontWeight: "bold" }}
                >
                  Menu Makanan
                </Link>
              </Button>

              <Button>
                <Link
                  to="/minuman"
                  className={classes.link}
                  style={{ color: valueTheme.color, fontWeight: "bold" }}
                >
                  Menu Minuman
                </Link>
              </Button>
              <Button
                style={{ color: valueTheme.color, fontWeight: "bold" }}
                className="Button"
                onClick={() =>
                  setValueTheme(
                    valueTheme === themes.light ? themes.dark : themes.light
                  )
                }
              >
                change theme
              </Button>
            </Router>
          </div>
        </Toolbar>
        <div className={classes.line}></div>
        <div>
          
          <p style={{ color: valueTheme.color }}><Header/></p>
        </div>
      </div>
    </ThemeContext.Provider>
  );
}

function Content(props) {
  return (
    <div>
      <Text tema={props.tema} />
    </div>
  );
}

function Text(props) {
  const theme = useContext(ThemeContext);
  return <p  style={{ color: theme.text }}></p>;
}
