import { useContext, createContext, useState } from "react";
import "./Context.css";
import Button from "@material-ui/core/Button";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { Toolbar } from "@material-ui/core";

const styles = makeStyles((theme) => ({
//   bar: {
//     backgroundColor: "#CAE4EA",
//     ["@media (max-width:780px)"]: {
//       flexDirection: "column",
//     },
//   },
  headerOptions: {
    display: "flex",
    flex: 1,
    justifyContent: "flex-end",
    ["@media (max-width:780px)"]: {
      flexDirection: "column",
    },
  },

  link: {
    textDecoration: "none",
    color: "black",
  },
}));

const themes = {
  light: {
    bar: {
        backgroundColor: "#CAE4EA",
        ["@media (max-width:780px)"]: {
          flexDirection: "column",
        },
      },
    background: "#DEE4E7",
  },
  dark: {
    bar: {
        backgroundColor: "rgb(40, 44, 52)",
        ["@media (max-width:780px)"]: {
          flexDirection: "column",
        },
      },
    background: "rgb(40, 44, 52)",
  },
};

const ThemeContext = createContext();

export default function Footer() {
  const classes = styles();
  const [valueTheme, setValueTheme] = useState(themes.dark);

  return (
    <ThemeContext.Provider value={valueTheme}>
      <Toolbar position="sticky" className={classes.bar}>
        <div>
          <Router>
            <Button>
              <Link to="/" className={classes.link}>
                {" "}
                Beranda
              </Link>
            </Button>

            <Button>
              <Link to="/makanan" className={classes.link}>
                Menu Makanan
              </Link>
            </Button>

            <Button>
              <Link to="/minuman" className={classes.link}>
                Menu Minuman
              </Link>
            </Button>
          </Router>
        </div>
      </Toolbar>
      <div
        className="contentWrapper"
        style={{ backgroundColor: valueTheme.background }}
      >
        <Content tema={valueTheme} />
        <button
          className="Button"
          onClick={() =>
            setValueTheme(
              valueTheme === themes.light ? themes.dark : themes.light
            )
          }
        >
          change
        </button>
      </div>
    </ThemeContext.Provider>
  );
}

function Content(props) {
  return (
    <div>
      {props.tema}
    </div>
  );
}
