import { useContext, createContext, useState } from "react";
import "./Context.css";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import InputBase from "@material-ui/core/InputBase";
import { BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
const themes = {
  light: {
    text: "rgb(97, 218, 60)",
    background: "#DEE4E7",
  },
  dark: {
    text: "#DEE4E7",
    background: "rgb(40, 44, 52)",
  },
};

const ThemeContext = createContext();

export default function Footer() {
  const [valueTheme, setValueTheme] = useState(themes.dark);

  return (
    <ThemeContext.Provider value={valueTheme}>
        <Toolbar position="sticky" className={classes.bar}>
      <div  >
          <Router>
        <Button
        >
          <Link to="/"  className={classes.link}  > Beranda</Link>
        </Button>

        <Button
          
        >
          <Link to="/makanan"   className={classes.link} >Menu Makanan</Link>
        </Button>

        <Button
          
        >
          <Link to="/minuman"  className={classes.link}  >Menu Minuman</Link>
        </Button>
       </Router>
      </div>
    </Toolbar>
      <div
        className="contentWrapper"
        style={{ backgroundColor: valueTheme.background }}
      >
        <Content tema={valueTheme} />
        <button
          className="Button"
          onClick={() =>
            setValueTheme(
              valueTheme === themes.light ? themes.dark : themes.light
            )
          }
        >
          change
        </button>
      </div>
    </ThemeContext.Provider>
  );
}

function Content(props) {
  return (
    <div>
      <Text tema={props.tema} />
    </div>
  );
}

function Text(props) {
  const theme = useContext(ThemeContext);
  return (
    <p
     
      className="titleContext"
      style={{ color: theme.text }}
    >
      context value {theme.text}
    </p>
  );
}
