import { useContext, createContext, useState } from "react";
import "./Context.css";
import Button from "@material-ui/core/Button";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { Toolbar } from "@material-ui/core";
import { mergeClasses } from "@material-ui/styles";

export default function Header() {
  const [valueInput, setValueInput] = useState({
    name: "",
  });

  const handleIdPembelian = (event, type) => {
    if (type === "name") {
      setValueInput((prevState) => {
        return { ...prevState, name: event.target.value };
      });
    }
  };

  return (
    <div className="container">
      <div style={{ margin: "10px" }}>
        <label for="idPembelian">Masukkan Nama</label>
        <br />
        <input
          onChange={(event) => handleIdPembelian(event, "name")}
          name="idPembelian"
          value={valueInput.name}
          style={{ color: "#6e0234", padding: "5px" }}
        />
        <marquee>Welcome {valueInput.name}</marquee>
      </div>
      <div>
        <h3>Selamat Datang, Silahkan Pilih Pesanan yang diinginkan</h3>
        <div>
          <h3>Makanan</h3>
          <img src="./images/makanan.jpg"></img>
        </div>
        <div>
          <h3>Minuman</h3>
        </div>
      </div>
    </div>
  );
}
